{
    "messages": {
        "authentication": {
            "producttoken": "{{ProductToken}}"
        },
        "msg": [
            {
                "from": "{{Sender}}",
                "to": [
                    {
                        "number": "{{Recipient}}"
                    }
                ],
                "body": {
                    "type": "auto",
                    "content": "Fallback Text"
                },
                "allowedChannels": [
                    "WhatsApp"
                ],
                "richContent": {
                    "conversation": [
                        {
                            "template": {
                                "whatsapp": {
                                    "namespace": "{{WA_Namespace}}",
                                    "element_name": "media_ticket",
                                    "language": {
                                        "policy": "deterministic",
                                        "code": "en"
                                    },
                                    "components": [
                                        {
                                            "type": "header",
                                            "parameters": [
                                                {
                                                    "type": "image",
                                                    "media": {
                                                        "mediaName": "Vind je dit wellicht leuk?",
                                                        "mediaUri": "https://www.cm.com/cdn/cm/cm.png",
                                                        "mimeType": "image/png"
                                                    }
                                                }
                                            ]
                                        },
                                        {
                                            "type": "body",
                                            "parameters": [
                                                {
                                                    "type": "text",
                                                    "text": "Name"
                                                },
                                                {
                                                    "type": "text",
                                                    "text": "Dutch GP"
                                                }
                                            ]
                                        }
                                    ]
                                }
                            }
                        }
                    ]
                }
            }
        ]
    }
}

